import React from 'react';
import { Container, Typography, Box, Button, RadioGroup, FormControlLabel, Radio } from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { Select, MenuItem } from '@mui/material';
import BackButton from '../components/BackButton'; // Import BackButton component

const SettingsPage = () => {
    const [selectedPercentage, setSelectedPercentage] = React.useState('');
    const [valveState, setValveState] = React.useState('closed'); // Default valve state: 'closed'

    const handlePercentageChange = (event) => {
        setSelectedPercentage(event.target.value);
    };

    const handleValveStateChange = (event) => {
        setValveState(event.target.value);
    };

    const handleSubmit = () => {
        alert(`Selected Valve Opening Amount: ${selectedPercentage}%\nValve is: ${valveState === 'open' ? 'Normally Open' : 'Normally Closed'}`);
    };

    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box sx={{ position: 'relative', height: '100vh', padding: 2 }}>
                <BackButton />
                <Container sx={{ textAlign: 'center', marginTop: '60px' }}>
                    <Typography variant="h6" sx={{
                        fontFamily: "'Nunito', sans-serif", 
                        color:'#84a7b8' // El yazısı fontu
                    }}>
                        Select Valve Opening Amount
                    </Typography>
                    <Box sx={{ marginBottom: 2 }}>
                      <Select
                        sx={{ 
                          maxWidth: '300px',  // Genişliği kısıtlayın
                          width: '100%',      // Mevcut alana göre genişlesin
                        }}
                        value={selectedPercentage}
                        onChange={handlePercentageChange}
                        displayEmpty
                        inputProps={{ 'aria-label': 'Select Percentage' }}
                      >
                        <MenuItem value="" disabled>Select Percentage</MenuItem>
                        {[...Array(20).keys()].map(i => {
                          const percentage = (i + 1) * 5; // 5'er 5'er artsın (5%, 10%, 15%, ...)
                          return (
                            <MenuItem key={percentage} value={percentage}>
                              {percentage}%
                            </MenuItem>
                          );
                        })}
                      </Select>
                    </Box>

                    {/* New Section for Valve State */}
                    <Typography variant="h6" sx={{
                        fontFamily: "'Nunito', sans-serif",
                        color:'#84a7b8'
                    }}>
                        Select Valve State
                    </Typography>
                    <RadioGroup
                        value={valveState}
                        onChange={handleValveStateChange}
                        sx={{ display: 'inline-block' }} // To make it centered and inline
                    >
                        <FormControlLabel value="open" control={<Radio />} label="Normally Open" />
                        <FormControlLabel value="closed" control={<Radio />} label="Normally Closed" />
                    </RadioGroup>

                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSubmit}
                        sx={{
                            marginTop: 2,
                            borderRadius: '50px',
                            background: 'linear-gradient(45deg, #84a7b8 30%, #aec1ca 90%)',
                            boxShadow: '0 6px 10px 4px rgba(110, 138, 152, .3)',
                            '&:hover': {
                                background: 'linear-gradient(45deg, #aec1ca 30%, #84a7b8 90%)',
                            },
                        }}
                    >
                        <Typography variant="h6" sx={{
                            fontFamily: "'Nunito', sans-serif",  // El yazısı fontu
                        }}>Submit</Typography>
                    </Button>
                </Container>
            </Box>
        </LocalizationProvider>
    );
};

export default SettingsPage;
