import React from 'react';
import { Container, Typography, Box, TextField, Button, Grid, Select, MenuItem } from '@mui/material';
import { LocalizationProvider, DatePicker, TimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';

const TimerPage = () => {
    const [selectedDate, setSelectedDate] = React.useState(null);
    const [selectedTime, setSelectedTime] = React.useState(null);
    const [selectedHours, setSelectedHours] = React.useState('');
    const [selectedMinutes, setSelectedMinutes] = React.useState('');

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const handleTimeChange = (time) => {
        setSelectedTime(time);
    };

    const handleHoursChange = (event) => {
        setSelectedHours(event.target.value);
    };

    const handleMinutesChange = (event) => {
        setSelectedMinutes(event.target.value);
    };

    const handleSubmit = () => {
        alert(`Selected Date and Time: ${selectedDate ? selectedDate.format('YYYY-MM-DD') : 'No Date'} ${selectedTime ? selectedTime.format('HH:mm:ss') : 'No Time'}\nSelected Hours: ${selectedHours}\nSelected Minutes: ${selectedMinutes}`);
    };

    const commonStyles = {
        width: '100%', // Make the width consistent across all components
        marginBottom: '16px',
    };

    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box sx={{ position: 'relative', height: '100vh', padding: 2 }}>
                <Typography
                    variant="h4"
                    sx={{
                        position: 'absolute',
                        top: 20,
                        left: '50%',
                        transform: 'translateX(-50%)',
                        zIndex: 1,
                    }}
                >
                    Timer Page
                </Typography>
                <Container sx={{ textAlign: 'center', marginTop: '60px', maxWidth: '400px' }}>
                    <Box sx={commonStyles}>
                        <DatePicker
                            label="Select Date"
                            value={selectedDate}
                            onChange={handleDateChange}
                            renderInput={(params) => <TextField {...params} fullWidth />}
                        />
                    </Box>
                    <Box sx={commonStyles}>
                        <TimePicker
                            label="Select Time"
                            value={selectedTime}
                            onChange={handleTimeChange}
                            renderInput={(params) => <TextField {...params} fullWidth />}
                        />
                    </Box>
                    <Box sx={commonStyles}>
                        <Typography variant="h6">Select Period</Typography>
                        <Grid container spacing={2}>
                            <Grid item xs={6}>
                                <Select
                                    fullWidth
                                    value={selectedHours}
                                    onChange={handleHoursChange}
                                    displayEmpty
                                >
                                    <MenuItem value="" disabled>Select Hours</MenuItem>
                                    {[...Array(24).keys()].map(hour => (
                                        <MenuItem key={hour} value={hour}>{hour} hour{hour > 1 ? 's' : ''}</MenuItem>
                                    ))}
                                </Select>
                            </Grid>
                            <Grid item xs={6}>
                                <Select
                                    fullWidth
                                    value={selectedMinutes}
                                    onChange={handleMinutesChange}
                                    displayEmpty
                                >
                                    <MenuItem value="" disabled>Select Minutes</MenuItem>
                                    {[...Array(60).keys()].map(minute => (
                                        <MenuItem key={minute} value={minute}>{minute} minute{minute > 1 ? 's' : ''}</MenuItem>
                                    ))}
                                </Select>
                            </Grid>
                        </Grid>
                    </Box>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSubmit}
                        sx={{
                            marginTop: 2,
                            borderRadius: '50px',
                            background: 'linear-gradient(45deg, #84a7b8 30%, #aec1ca 90%)',
                            boxShadow: '0 6px 10px 4px rgba(110, 138, 152, .3)',
                            '&:hover': {
                                background: 'linear-gradient(45deg, #aec1ca 30%, #84a7b8 90%)',
                            },
                        }}
                    >
                        Submit
                    </Button>
                </Container>
            </Box>
        </LocalizationProvider>
    );
};

export default TimerPage;
