import React from 'react';
import { Container, Typography, Box, TextField, Button } from '@mui/material';
import { LocalizationProvider, DatePicker, TimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';

const TimerPage = () => {
    const [selectedDate, setSelectedDate] = React.useState(null);
    const [selectedTime, setSelectedTime] = React.useState(null);

    const handleDateChange = (date) => {
      setSelectedDate(date);
    };

    const handleTimeChange = (time) => {
      setSelectedTime(time);
    };

    const handleSubmit = () => {
      alert(`Selected Date and Time: ${selectedDate ? selectedDate.format('YYYY-MM-DD') : 'No Date'} ${selectedTime ? selectedTime.format('HH:mm:ss') : 'No Time'}`);
    };

    return (
      <LocalizationProvider dateAdapter={AdapterDayjs}>
        <Box sx={{ position: 'relative', height: '100vh', padding: 2 }}>
          <Typography
            variant="h4"
            sx={{
              position: 'absolute',
              top: 20,
              left: '50%',
              transform: 'translateX(-50%)',
              zIndex: 1,
            }}
          >
            Timer Page
          </Typography>
          <Container sx={{ textAlign: 'center', marginTop: '60px' }}>
            <Box sx={{ marginBottom: 2 }}>
              <DatePicker
                label="Select Date"
                value={selectedDate}
                onChange={handleDateChange}
                renderInput={(params) => <TextField {...params} />}
              />
            </Box>
            <Box sx={{ marginBottom: 2 }}>
              <TimePicker
                label="Select Time"
                value={selectedTime}
                onChange={handleTimeChange}
                renderInput={(params) => <TextField {...params} />}
              />
            </Box>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              sx={{
                marginTop: 2,
                borderRadius: '50px',
                background: 'linear-gradient(45deg, #84a7b8 30%, #aec1ca 90%)',
                boxShadow: '0 6px 10px 4px rgba(110, 138, 152, .3)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #aec1ca 30%, #84a7b8 90%)',
                },
              }}
            >
              Submit
            </Button>
          </Container>
        </Box>
      </LocalizationProvider>
    );
  };

  export default TimerPage;
